# Import the necessary modules from the NagadPy package
from nagadpy import settings, payment

# Customize NagadPy settings using environment variables or direct assignments
settings.NAGAD_BASE_URL = "http://sandbox.mynagad.com:10080"
settings.NAGAD_MERCHANT_ID = "683002007104225"
settings.NAGAD_PUBLIC_KEY = ""
settings.NAGAD_PRIVATE_KEY = ""

settings.NAGAD_MERCHANT_CALLBACK_URL = "your_callback_url"
settings.NAGAD_INTIATE_PAYMENT_URL = "initiate_payment"
settings.NAGAD_COMPLETE_PAYMENT_URL = "complete_payment"

# Define the invoice number and host IP for testing
invoice_number = "invoice123"
host_ip = "127.0.0.1"

# Test the payment initiation
result, error = payment.nagad_checkout_process(invoice_number, "100", host_ip)

if error:
    print("Error:", error)
else:
    print("Initiate Payment Result:", result)

